import React, { useState } from 'react';
import { useTranslation } from 'react-i18next';
import RegistrationForm from '../RegistrationForm/RegistrationForm';
import LoginForm from '../LoginForm/LoginForm';
import '../../styles/global.css';
import './Homepage.css';

const Homepage = ({ onRegister, onLogin, registerError, loginError }) => {
  const { t } = useTranslation();
  const [showRegister, setShowRegister] = useState(false);
  const [showLogin, setShowLogin] = useState(false);

  const handleShowRegister = () => {
    setShowRegister(true);
    setShowLogin(false);
  };

  const handleShowLogin = () => {
    setShowLogin(true);
    setShowRegister(false);
  };

  return (
    <div className="homepage">
      <header>
        <h1>{t('welcome')}</h1>
        <p>{t('homepage_intro')}</p>
      </header>
      {!showRegister && !showLogin && (
        <div className="buttons">
          <button onClick={handleShowRegister} aria-label={t('register')}>{t('register')}</button>
          <button onClick={handleShowLogin} aria-label={t('login')}>{t('login')}</button>
        </div>
      )}
      {showRegister && (
        <>
          <RegistrationForm onRegister={onRegister} error={registerError} onShowLogin={handleShowLogin} />
          <button onClick={() => setShowRegister(false)}>{t('back_to_homepage')}</button>
        </>
      )}
      {showLogin && (
        <>
          <LoginForm onLogin={onLogin} error={loginError} onShowRegister={handleShowRegister} />
          <button onClick={() => setShowLogin(false)}>{t('back_to_homepage')}</button>
        </>
      )}
    </div>
  );
};

export default Homepage;
