import React, { useState } from 'react';
import { useTranslation } from 'react-i18next';
import axios from 'axios';
import '../../styles/global.css';
import './RegistrationForm.css';

// Updated topics structure to match your course list
const topics = {
  'Science': ['Physics', 'Quantum Physics', 'Biology', 'Chemistry'],
  'Computer Science': ['Artificial Intelligence', 'Python Programming', 'Java Programming', 'Data Science'],
  'Engineering': ['Mechanical Engineering', 'Electrical Engineering', 'Civil Engineering', 'Robotics'],
  'Mathematics': ['Algebra', 'Geometry', 'Calculus', 'Statistics'],
  'Business': ['Business Analytics', 'Marketing', 'Finance', 'Entrepreneurship'],
  'Humanities': ['History', 'Philosophy', 'Psychology'],
  'Art': ['Painting', 'Sculpture', 'Photography', 'Music'],
  'Languages': ['English', 'Spanish', 'French', 'Mandarin'],
};

// Define available difficulty levels
const difficultyLevels = ['Beginner', 'Intermediate', 'Advanced']; // Define difficulty levels

const RegistrationForm = ({ onRegister, onShowLogin }) => {
  const { t } = useTranslation();
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [language, setLanguage] = useState('');
  const [selectedTopics, setSelectedTopics] = useState({});
  const [difficultyLevelsPerTopic, setDifficultyLevelsPerTopic] = useState({});  // Store difficulty levels per topic

const handleSubmit = async (e) => {
  e.preventDefault();
  if (password !== confirmPassword) {
    alert(t('passwords_do_not_match'));
    return;
  }

  // Ensure selectedTopics and difficultyLevels are correctly populated
  const userData = {
    name,
    email,
    username,
    password,
    language,
    preferences: { selectedTopics, difficultyLevelsPerTopic }
  };

  // Debug log to check the structure of the data being sent
  console.log('User Data:', userData);

  // Send the data to the backend
  await axios.post('/api/register', userData);  // Send user data with preferences
  onRegister(userData);
};

// Handle topic selection (select/deselect all sub-topics)
const handleTopicChange = (topic) => {
  const newSelectedTopics = { ...selectedTopics };
  if (!newSelectedTopics[topic]) {
    newSelectedTopics[topic] = [];
  }

  // Toggle topic selection: If topic is selected, all sub-topics will be selected
  if (newSelectedTopics[topic].length === 0) {
    newSelectedTopics[topic] = topics[topic];  // Select all sub-topics for the topic
  } else {
    newSelectedTopics[topic] = [];  // Deselect all sub-topics
  }

  setSelectedTopics(newSelectedTopics);
};

// Handle sub-topic selection (select/deselect individual sub-topic)
const handleSubTopicChange = (topic, subTopic) => {
  const newSelectedTopics = { ...selectedTopics };
  if (!newSelectedTopics[topic]) {
    newSelectedTopics[topic] = [];
  }

  if (newSelectedTopics[topic].includes(subTopic)) {
    // Deselect sub-topic
    newSelectedTopics[topic] = newSelectedTopics[topic].filter(item => item !== subTopic);
  } else {
    // Select sub-topic
    newSelectedTopics[topic].push(subTopic);
  }

  // If all sub-topics are selected, select the parent topic
  if (newSelectedTopics[topic].length === topics[topic].length) {
    // Select the parent topic when all sub-topics are selected
    newSelectedTopics[topic] = topics[topic];
  }

  setSelectedTopics(newSelectedTopics);
};

const handleDifficultyChange = (topic, subTopic, level) => {
    const newDifficultyLevels = { ...difficultyLevelsPerTopic };
    newDifficultyLevels[`${topic}_${subTopic}`] = level;  // Store difficulty per topic
    setDifficultyLevelsPerTopic(newDifficultyLevels);
};

return (
    <div className="registration-form">
      <h2>{t('register')}</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>{t('name')}</label>
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
        </div>
        <div className="form-group">
          <label>{t('email')}</label>
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
        </div>
        <div className="form-group">
          <label>{t('username')}</label>
          <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} required />
        </div>
        <div className="form-group">
          <label>{t('password')}</label>
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
        </div>
        <div className="form-group">
          <label>{t('confirm_password')}</label>
          <input type="password" value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)} required />
        </div>
        <div className="form-group">
          <label>{t('preferred_language')}</label>
          <select value={language} onChange={(e) => setLanguage(e.target.value)} required>
            <option value="">{t('select_language')}</option>
            <option value="id">Indonesian</option>
            <option value="hi">Hindi</option>
            <option value="en">English</option>
          </select>
        </div>
        {/* Topics Selection */}
        <div className="form-group">
          <label>{t('interest_topics')}</label>
          <div className="topics-container">
            {Object.keys(topics).map((topic) => (
              <div key={topic}>
                <label>
                  <input
                    type="checkbox"
                    checked={selectedTopics[topic] && selectedTopics[topic].length === topics[topic].length}
                    onChange={() => handleTopicChange(topic)}
                  />
                  {topic}
                </label>
                {topics[topic].map((subTopic) => (
                  <div key={subTopic}>
                    <input
                      type="checkbox"
                      checked={selectedTopics[topic] && selectedTopics[topic].includes(subTopic)}
                      onChange={() => handleSubTopicChange(topic, subTopic)}
                    />
                    <label>{subTopic}</label>
                    {/* Difficulty Level Dropdown per Topic */}
                    <select
                      onChange={(e) => handleDifficultyChange(topic, subTopic, e.target.value)}
                      value={difficultyLevelsPerTopic[`${topic}_${subTopic}`] || 'Beginner'}
                    >
                      {difficultyLevels.map((level) => (
                        <option key={level} value={level}>{level}</option>
                      ))}
                    </select>
                  </div>
                ))}
              </div>
            ))}
          </div>
        </div>
        <button type="submit">{t('register')}</button>
      </form>
      <p>{t('already_have_account')} <a href="#" onClick={onShowLogin}>{t('login_here')}</a></p>
      <p><a href="#" onClick={() => window.location.href = '/'}>{t('back_to_homepage')}</a></p>
    </div>
  );
};

export default RegistrationForm;
