import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import axios from 'axios';
import '../../styles/global.css';
import './VideoPlayer.css';

const VideoPlayer = () => {
  const { t, i18n } = useTranslation();
  const location = useLocation();
  const navigate = useNavigate();
  const { course } = location.state || {}; // Expect `state` to include course details
  const [description, setDescription] = useState(''); // For course description
  const [translatedNotes, setTranslatedNotes] = useState(''); // For translated notes

  useEffect(() => {
    if (course?.description) {
      // Fetch the description content
      axios.get(course.description)
        .then((response) => {
          setDescription(response.data); // Set the raw description content
        })
        .catch((error) => console.error('Error fetching description:', error));
    }

    if (course?.content?.text?.url) {
      // Fetch and translate the text notes content
      axios.get(course.content.text.url)
        .then((response) => {
          const rawNotes = response.data;

          // Translate notes if not in the current language
          if (i18n.language !== course.language) {
            axios.post('/api/translate-interface', {
              text: rawNotes,
              targetLanguage: i18n.language,
            })
              .then((translationResponse) => {
                setTranslatedNotes(translationResponse.data.translatedText);
              })
              .catch((error) => console.error('Error translating notes:', error));
          } else {
            setTranslatedNotes(rawNotes); // Use raw notes if no translation is needed
          }
        })
        .catch((error) => console.error('Error fetching notes:', error));
    }
  }, [course, i18n.language]);

  if (!course) {
    return <div>{t('course_data_not_available')}</div>;
  }

  return (
    <div className="video-player">
      <nav className="breadcrumb">
        {/* Navigate back to course selection with preserved preferences */}
        <a href="#" onClick={() => navigate('/courses')}>
          {t('back_to_course_selection')}
        </a>
      </nav>
      <h2>{course.title}</h2>

      {/* Video container */}
      <div className="video-container">
        <video
          width="100%"
          height="480"
          controls
          src={course.content?.video?.url || ''}
        >
          {t('your_browser_does_not_support_video')}
        </video>
      </div>

      {/* Course details */}
      <div className="course-details">
        <p><strong>{t('duration')}:</strong> {course.content?.video?.duration || t('not_available')}</p>
        <p><strong>{t('level')}:</strong> {course.level}</p>
        <p><strong>{t('source')}:</strong> {course.source}</p>
        <div className="course-topics">
          {course.topics.map((topic) => (
            <span key={topic} className="course-topic">{topic}</span>
          ))}
        </div>
      </div>

      {/* Course description */}
      <div className="course-description">
        <h3>{t('description')}</h3>
        <p>{description || t('not_available')}</p>
      </div>

      {/* Notes section */}
      <div className="course-notes">
        <h3>{t('notes')}</h3>
        <div className="notes-content">
          {translatedNotes || t('not_available')}
        </div>
      </div>
    </div>
  );
};

export default VideoPlayer;
