import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import '../../styles/global.css';
import './VideoPlayer.css';

const VideoPlayer = () => {
  const { t } = useTranslation();
  const location = useLocation();
  const navigate = useNavigate();
  const { course } = location.state || {}; // Expect `state` to include course details

  if (!course) {
    return <div>{t('course_data_not_available')}</div>;
  }

  const videoUrl = course.content?.video?.url || '';
  const videoDuration = course.content?.video?.duration || t('not_available');
  const textContentUrl = course.content?.text?.url || '';
  const courseDescription = course.description || t('not_available');

  console.log('videoUrl', videoUrl)

  return (
    <div className="video-player">
      <nav className="breadcrumb">
        <a href="#" onClick={() => navigate('/courses')}>
          {t('back_to_course_selection')}
        </a>
      </nav>
      <h2>{course.title}</h2>

      {/* Video container */}
      <div className="video-container">
        {videoUrl ? (
          <video width="100%" height="480" controls src={videoUrl}>
            {t('your_browser_does_not_support_video')}
          </video>
        ) : (
          <p>{t('video_not_available')}</p>
        )}
      </div>

      {/* Course details */}
      <div className="course-details">
        <p><strong>{t('description')}:</strong> {courseDescription}</p>
        <p><strong>{t('duration')}:</strong> {videoDuration}</p>
        <p><strong>{t('level')}:</strong> {course.level || t('not_available')}</p>
        <p><strong>{t('source')}:</strong> {course.source || t('not_available')}</p>
        <p className="course-topics">
          {course.topics && course.topics.length > 0
            ? course.topics.map(topic => (
                <span key={topic} className="course-topic">{topic}</span>
              ))
            : t('no_topics')}
        </p>
      </div>

      {/* Course content */}
      <div className="course-content">
        <h3>{t('notes')}</h3>
        {textContentUrl ? (
          <iframe
            src={textContentUrl}
            width="100%"
            height="200"
            frameBorder="0"
            title={t('course_content')}
            className="content-frame"
          ></iframe>
        ) : (
          <p>{t('text_content_not_available')}</p>
        )}
      </div>
    </div>
  );
};

export default VideoPlayer;
