import React, { useState, useEffect } from 'react';
import { useTranslation } from 'react-i18next';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../../styles/global.css';
import './VideoPlayer.css';

const VideoPlayer = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { course } = location.state;
  const [captions, setCaptions] = useState([]);

  useEffect(() => {
    // Fetch captions from backend
    axios.get(`/api/captions?videoId=${course.id}`)
      .then(response => setCaptions(response.data))
      .catch(error => console.error('Error fetching captions:', error));
  }, [course]);

  return (
    <div className="video-player">
      <nav className="breadcrumb">
        <a href="#" onClick={() => navigate(-1)}>{t('back_to_course_selection')}</a>
      </nav>
      <h2>{course.title}</h2>
      <video controls>
        <source src={course.url} type="video/mp4" />
        {captions.map(caption => (
          <track key={caption.language} kind="captions" srcLang={caption.language} src={caption.url} />
        ))}
      </video>
      <div className="course-details">
        <p><strong>Duration:</strong> {course.duration}</p>
        <p><strong>Level:</strong> {course.level}</p>
        <p><strong>Source:</strong> {course.source}</p>
        <div className="course-topics">
          {course.topics.map(topic => (
            <span key={topic} className="course-topic">{topic}</span>
          ))}
        </div>
      </div>
    </div>
  );
};

export default VideoPlayer;
