import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import axios from 'axios';
import '../../styles/global.css';
import './VideoPlayer.css';

const VideoPlayer = () => {
  const { t, i18n } = useTranslation();
  const location = useLocation();
  const navigate = useNavigate();
  const { course } = location.state || {}; // Expect `state` to include course details     
  const [translatedDescription, setTranslatedDescription] = useState(''); // Translated description
  const [translatedNotes, setTranslatedNotes] = useState(''); // For translated notes
  const [description, setDescription] = useState('');

  // Fetch the text content from the URL
  useEffect(() => {
    if (course?.description) {
      // Fetch the description content
      axios.get(course.description)
        .then((response) => {
          const rawDescription = response.data;

          // Translate description if necessary
          if (i18n.language !== course.language) {
            axios.post('/api/translate-interface', {
              text: rawDescription,
              targetLanguage: i18n.language,
            })
              .then((translationResponse) => {
                setTranslatedDescription(translationResponse.data.translatedText);
              })
              .catch((error) => console.error('Error translating description:', error));
          } else {
            setTranslatedDescription(rawDescription); // Use raw description if no translation needed
          }

          setDescription(rawDescription);
        })
        .catch((error) => console.error('Error fetching description:', error));
    }

    if (course._doc.content?.text?.url) {
      axios
        .get(course._doc.content.text.url)
        .then((response) => {
            const rawNotes = response.data;

            // Translate notes if not in the current language
            if (i18n.language !== course._doc.language) {
              axios.post('/api/translate-interface', {
                text: rawNotes,
                targetLanguage: i18n.language,
              })
                .then((translationResponse) => {
                  setTranslatedNotes(translationResponse.data.translatedText);
                })
                .catch((error) => console.error('Error translating notes:', error));
            } else {
              setTranslatedNotes(rawNotes); // Use raw notes if no translation is needed
            }
        })
        .catch((error) => {
          console.error('Error fetching text content:', error);
          setTextContent(t('error_loading_content')); // Fallback message
        });
    }
  }, [course, i18n.language]);

  if (!course) {
    return <div>{t('course_data_not_available')}</div>;
  }

  return (
    <div className="video-player">
      <nav className="breadcrumb">
        <a href="#" onClick={() => navigate('/courses')}>
          {t('back_to_course_selection')}
        </a>
      </nav>
      <h2>{course.title}</h2>

      {/* Video container */}
      <div className="video-container">
        <video
          width="100%"
          height="480"
          controls
          src={course._doc.content?.video?.url || ''}
        >
          {t('your_browser_does_not_support_video')}
        </video>
      </div>

      {/* Course details */}
      <div className="course-details">
        <p><strong>{t('duration')}:</strong> {course._doc.content?.video?.duration || t('not_available')}</p>
        <p><strong>{t('level')}:</strong> {course._doc.level}</p>
        <p><strong>{t('source')}:</strong> {course._doc.source}</p>
        <p className="course-topics">
          {course._doc.topics.map((topic) => (
            <span key={topic} className="course-topic">{topic}</span>
          ))}
        </p>
      </div>

        {/* Course description */}
        <div className="course-description">
            <h3>{t('description')}</h3>
            <p>{translatedDescription || t('not_available')}</p>
        </div>

      {/* Notes section */}
      <div className="course-notes">
        <h3>{t('notes')}</h3>
        <div className="notes-content">
            {translatedNotes.split('\n').map((line, index) => (
              <p key={index} style={{ marginBottom: '8px' }}>
                {line}
              </p>
            ))}
        </div>
      </div>
    </div>
  );
};

export default VideoPlayer;
