import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import axios from 'axios';
import '../../styles/global.css';
import './VideoPlayer.css';

const VideoPlayer = () => {
  const { t, i18n } = useTranslation();
  const location = useLocation();
  const navigate = useNavigate();
  const { course } = location.state || {}; // Expect `state` to include course details
  const [notes, setNotes] = useState('');

    useEffect(() => {
      if (course?._doc?.content?.text?.url) {
        axios.get(course._doc.content.text.url)
          .then(response => {
            setNotes(response.data); // Load notes content from text file
          })
          .catch(error => {
            console.error('Error fetching notes:', error.message);
            setNotes(t('notes_not_available')); // Fallback message
          });
      }
    }, [course, t]);

  if (!course) {
    return <div>{t('course_data_not_available')}</div>;
  }

  return (
    <div className="video-player">
      <nav className="breadcrumb">
        <a href="#" onClick={() => navigate('/courses')}>
          {t('back_to_course_selection')}
        </a>
      </nav>
      <h2>{course.title}</h2>

      {/* Video container */}
      <div className="video-container">
        <video
          width="100%"
          height="480"
          controls
          src={course._doc.content?.video?.url || ''}
        >
          {t('your_browser_does_not_support_video')}
        </video>
      </div>

      {/* Course details */}
      <div className="course-details">
        <p><strong>{t('duration')}:</strong> {course._doc.content?.video?.duration || t('not_available')}</p>
        <p><strong>{t('level')}:</strong> {course._doc.level}</p>
        <p><strong>{t('source')}:</strong> {course._doc.source}</p>
        <p className="course-topics">
          {course._doc.topics.map((topic) => (
            <span key={topic} className="course-topic">{topic}</span>
          ))}
        </p>
      </div>

        {/* Course description */}
        <div className="course-description">
            <h3>{t('description')}</h3>
            <p>{translatedDescription || t('not_available')}</p>
        </div>

      {/* Notes section */}
      <div className="course-notes">
        <h3>{t('notes')}</h3>
        <div className="notes-content">
            {translatedNotes.split('\n').map((line, index) => (
              <p key={index} style={{ marginBottom: '8px' }}>
                {line}
              </p>
            ))}
        </div>
      </div>
    </div>
  );
};

export default VideoPlayer;
