import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import Homepage from './components/Homepage/Homepage';
import ContentSelection from './components/ContentSelection/ContentSelection';
import VideoPlayer from './components/VideoPlayer/VideoPlayer';
import FeedbackForm from './components/FeedbackForm/FeedbackForm';
import './styles/global.css';
import './App.css';

const App = () => {
  const { t, i18n } = useTranslation();
  const [user, setUser] = useState(null);
  const [selectedVideo, setSelectedVideo] = useState(null);

  useEffect(() => {
    // Force language change for testing
    i18n.changeLanguage('id'); // Change 'id' to 'hi' or 'en' to test other languages
  }, [i18n]);

  console.log('Current language:', i18n.language); // Add this line
  console.log('Translation for welcome:', t('welcome')); // Add this line

  const handleRegister = (userData) => {
    setUser(userData);
  };

  const handleLogin = (userData) => {
    setUser(userData);
  };

  const handleSelectVideo = (video) => {
    setSelectedVideo(video);
  };

  const handleLogout = () => {
    setUser(null);
    setSelectedVideo(null);
  };

  return (
    <Router>
      <div>
        <header>
          <h1>{t('welcome')}</h1>
          {user && (
            <div className="user-info">
              <span>{user.username}</span>
              <a href="#" onClick={handleLogout}>{t('logout')}</a>
            </div>
          )}
        </header>
        <Routes>
          <Route path="/" element={
            !user ? <Homepage onRegister={handleRegister} onLogin={handleLogin} /> :
            !selectedVideo ? <ContentSelection username={user.username} onSelect={handleSelectVideo} /> :
            null
          } />
          <Route path="/video-player" element={<VideoPlayer video={selectedVideo} />} />
        </Routes>
        <footer>
          <p>Â©2024 by Team R4131N. AI for Impact Hackathon</p>
        </footer>
      </div>
    </Router>
  );
};

export default App;
