import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import { io } from 'socket.io-client';
import axios from 'axios';
import '../../styles/global.css';
import './VideoPlayer.css';

const VideoPlayer = () => {
  const { t, i18n } = useTranslation();
  const location = useLocation();
  const navigate = useNavigate();
  const { course } = location.state || {}; // Expect `state` to include course details
  const [captions, setCaptions] = useState('');
  const [socket, setSocket] = useState(null);

  const videoUrl = course._doc?.content?.video?.url || '';
  const videoDuration = course._doc?.content?.video?.duration || t('not_available');
  const translatedTitle = course.title?.[i18n.language] || course.title?.en || t('not_available');
  const translatedDescription = course.description?.[i18n.language] || course.description?.en || t('not_available');
  const translatedNotes = course.notes?.[i18n.language] || course.notes.en || t('not_available');

  console.log('notes', course)

    useEffect(() => {
      // Initialize Socket.IO connection
      const newSocket = io('http://localhost:5001');

      // Emit user language preference to the server
      const userLanguage = localStorage.getItem('preferredLanguage') || 'en'; // Default to 'en' if no preference is set
      newSocket.emit('setLanguage', userLanguage);

      setSocket(newSocket);

      // Clean up on component unmount
      return () => {
        if (socket) {
          socket.disconnect();
        }
      };
    }, [socket]); // Add socket to the dependency array


  if (!course) {
    return <div>{t('course_data_not_available')}</div>;
  }

  return (
    <div className="video-player">
      <nav className="breadcrumb">
        <a href="#" onClick={() => navigate('/courses')}>
          {t('back_to_course_selection')}
        </a>
      </nav>
      <h2>{course.title}</h2>

      {/* Video container */}
      <div className="video-container">
        <video
          width="100%"
          height="480"
          controls
          src={course._doc?.content?.video?.url || ''}
        >
          {t('your_browser_does_not_support_video')}
        </video>
      </div>

        {/* Course description */}
        <div className="course-description">
            <h3>{t('description')}</h3>
            <p>{course.description || t('not_available')}</p>
              {/* Course details */}
              <div className="course-details">
                <p><strong>{t('duration')}:</strong> {course._doc?.content?.video?.duration || t('not_available')}</p>
                <p><strong>{t('level')}:</strong> {course._doc?.level}</p>
                <p><strong>{t('source')}:</strong> {course._doc?.source}</p>
                <p className="course-topics"><strong>{t('topic')}:</strong>
                  {course._doc?.topics.map((topic) => (
                    <span key={topic} className="course-topic">{topic}</span>
                  ))}
                </p>
              </div>
        </div>

      {/* Notes section */}
      <div className="course-notes">
        <h3>{t('notes')}</h3>
        <div className="notes-content">
            {course.notes.split('\n').map((line, index) => (
              <p key={index} style={{ marginBottom: '8px' }}>
                {line}
              </p>
            ))}
        </div>
      </div>
    </div>
  );
};

export default VideoPlayer;
