import React, { useState } from 'react';
import { useTranslation } from 'react-i18next';
import axios from 'axios';
import '../../styles/global.css';
import './RegistrationForm.css';

// Updated topics structure to match your course list
const topics = {
  'Science': ['Physics', 'Quantum Physics', 'Biology', 'Chemistry'],
  'Computer Science': ['Artificial Intelligence', 'Python Programming', 'Java Programming', 'Data Science'],
  'Engineering': ['Mechanical Engineering', 'Electrical Engineering', 'Civil Engineering', 'Robotics'],
  'Mathematics': ['Algebra', 'Geometry', 'Calculus', 'Statistics'],
  'Business': ['Business Analytics', 'Marketing', 'Finance', 'Entrepreneurship'],
  'Humanities': ['History', 'Philosophy', 'Psychology'],
  'Art': ['Painting', 'Sculpture', 'Photography', 'Music'],
  'Languages': ['English', 'Spanish', 'French', 'Mandarin'],
};

const RegistrationForm = ({ onRegister, onShowLogin }) => {
  const { t } = useTranslation();
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [language, setLanguage] = useState('');
  const [interests, setInterests] = useState({
    'Science': [],
    'Computer Science': [],
    'Engineering': [],
    'Mathematics': [],
    'Business': [],
    'Humanities': [],
    'Art': [],
    'Languages': [],
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      alert(t('passwords_do_not_match'));
      return;
    }
    const flattenedInterests = Object.keys(interests).flatMap(topic => interests[topic]);
    const userData = { name, email, username, password, language, preferences: flattenedInterests };
    // Send registration data to the backend
    await axios.post('/api/register', userData);
    // Translate interface to user's preferred language
    await axios.post('/api/translate-interface', { language });
    onRegister(userData);
  };

  const handleTopicChange = (topic) => {
    const newInterests = { ...interests };
    if (newInterests[topic] && newInterests[topic].length === topics[topic].length) {
      newInterests[topic] = [];
    } else {
      newInterests[topic] = topics[topic];
    }
    setInterests(newInterests);
  };

  const handleSubTopicChange = (topic, subTopic) => {
    const newInterests = { ...interests };

    // Initialize the topic array if it's undefined
    if (!newInterests[topic]) {
      newInterests[topic] = [];
    }

    if (newInterests[topic].includes(subTopic)) {
      newInterests[topic] = newInterests[topic].filter(item => item !== subTopic);
    } else {
      newInterests[topic].push(subTopic);
    }

    setInterests(newInterests);
  };

  return (
    <div className="registration-form">
      <h2>{t('register')}</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>{t('name')}</label>
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
        </div>
        <div className="form-group">
          <label>{t('email')}</label>
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
        </div>
        <div className="form-group">
          <label>{t('username')}</label>
          <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} required />
        </div>
        <div className="form-group">
          <label>{t('password')}</label>
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
        </div>
        <div className="form-group">
          <label>{t('confirm_password')}</label>
          <input type="password" value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)} required />
        </div>
        <div className="form-group">
          <label>{t('preferred_language')}</label>
          <select value={language} onChange={(e) => setLanguage(e.target.value)} required>
            <option value="">{t('select_language')}</option>
            <option value="id">Indonesian</option>
            <option value="hi">Hindi</option>
            <option value="en">English</option>
          </select>
        </div>
        {/* Topics Selection */}
        <div className="form-group">
          <label>{t('interest_topics')}</label>
          <div className="topics-container">
            {Object.keys(topics).map((topic) => (
              <div key={topic}>
                <label>{topic}</label>
                {topics[topic].map((subTopic) => (
                  <div key={subTopic}>
                    <input
                      type="checkbox"
                      checked={selectedTopics[topic] && selectedTopics[topic].includes(subTopic)}
                      onChange={() => handleTopicChange(topic, subTopic)}
                    />
                    <label>{subTopic}</label>
                  </div>
                ))}
              </div>
            ))}
          </div>
        </div>
        {/* Difficulty Level Selection */}
        <div className="form-group">
          <label>{t('difficulty_level')}</label>
          <select value={difficulty} onChange={(e) => setDifficulty(e.target.value)} required>
            {difficultyLevels.map((level) => (
              <option key={level} value={level}>{level}</option>
            ))}
          </select>
        </div>
        <button type="submit">{t('register')}</button>
      </form>
      <p>{t('already_have_account')} <a href="#" onClick={onShowLogin}>{t('login_here')}</a></p>
      <p><a href="#" onClick={() => window.location.href = '/'}>{t('back_to_homepage')}</a></p>
    </div>
  );
};

export default RegistrationForm;
