import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import axios from 'axios';
import '../../styles/global.css';
import './VideoPlayer.css';

const VideoPlayer = () => {
  const { t, i18n } = useTranslation();
  const location = useLocation();
  const navigate = useNavigate();
  const { course } = location.state || {}; // Expect `state` to include course details
  const [captions, setCaptions] = useState([]);
  const [currentCaption, setCurrentCaption] = useState('');

  useEffect(() => {
    if (course) {
      // Fetch captions from backend
      axios.get(`/api/captions?videoId='lkQT8zb0_A8''`)
        .then(response => setCaptions(response.data))
        .catch(error => console.error('Error fetching captions:', error));
    }
  }, [course]);

  useEffect(() => {
    const interval = setInterval(() => {
      if (captions.length > 0) {
        const currentTime = Math.floor(new Date().getTime() / 1000); // Get current time in seconds
        const currentCaption = captions.find(caption =>
          currentTime >= caption.startTime && currentTime <= caption.endTime
        );

        if (currentCaption) {
          setCurrentCaption(currentCaption.text);
        }
      }
    }, 1000); // Update every second

    return () => clearInterval(interval); // Clean up the interval when the component unmounts
  }, [captions]);

  if (!course) {
    return <div>{t('course_data_not_available')}</div>;
  }

  return (
    <div className="video-player-container">
      <nav className="breadcrumb">
        {/* Navigate back to course selection */}
        <a href="#" onClick={() => navigate('/courses')}>{t('back_to_course_selection')}</a>
      </nav>
      <div className="video-and-captions">
        <div className="video-container">
          <iframe
            width="100%"
            height="480"
            src={`https://www.youtube.com/embed/${course._doc.url.split('v=')[1]}?cc_load_policy=1&cc_lang_pref=${i18n.language}&controls=0&rel=0`}
            frameBorder="0"
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
            allowFullScreen
            title={course.title}
          ></iframe>
        </div>
        <div className="captions-container">
          <h3>{t('captions')}</h3>
          <p className="caption-text">{currentCaption}</p>
        </div>
      </div>
      <div className="course-details">
        <p><strong>{t('duration')}:</strong> {course._doc.duration}</p>
        <p><strong>{t('level')}:</strong> {course._doc.level}</p>
        <p><strong>{t('source')}:</strong> {course._doc.source}</p>
        <div className="course-topics">
          {course._doc.topics.map(topic => (
            <span key={topic} className="course-topic">{topic}</span>
          ))}
        </div>
      </div>
    </div>
  );
};

export default VideoPlayer;
