import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../../styles/global.css';
import './ContentSelection.css';

const ContentSelection = ({ username }) => {
  const [courses, setCourses] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredCourses, setFilteredCourses] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch course list from backend
    axios.get('/api/courses', { params: { username } })
      .then(response => {
        setCourses(response.data);
        setFilteredCourses(response.data);
      })
      .catch(error => console.error('Error fetching courses:', error));
  }, [username]);

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
    const filtered = courses.filter(course =>
      course.title.toLowerCase().includes(e.target.value.toLowerCase())
    );
    setFilteredCourses(filtered);
  };

  const handleSelectCourse = (course) => {
    navigate('/video-player', { state: { course } });
  };

  return (
    <div className="content-selection">
      <h2>Select a Course</h2>
      <input
        type="text"
        placeholder="Search courses..."
        value={searchTerm}
        onChange={handleSearch}
      />
      <div className="course-grid">
        {filteredCourses.map(course => (
          <div key={course.id} className="course-card" onClick={() => handleSelectCourse(course)}>
            <img src={course.thumbnail} alt={course.title} className="course-thumbnail" />
            <h3>{course.title}</h3>
            <p><strong>Duration:</strong> {course.duration}</p>
            <p><strong>Level:</strong> {course.level}</p>
            <p><strong>Source:</strong> {course.source}</p>
            <div className="course-topics">
              {course.topics.map(topic => (
                <span key={topic} className="course-topic">{topic}</span>
              ))}
            </div>
            <div className="course-feedback">
              {/* Placeholder for feedback stars */}
              <span>⭐⭐⭐⭐⭐</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ContentSelection;
