import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import axios from 'axios';
import '../../styles/global.css';
import './VideoPlayer.css';

const VideoPlayer = () => {
  const { t, i18n } = useTranslation();
  const location = useLocation();
  const navigate = useNavigate();
  const { course } = location.state || {}; // Expect `state` to include course details
  const [captions, setCaptions] = useState([]);

  useEffect(() => {
    console.log("Course", course)
    if (course) {
      // Fetch captions from backend
      axios.get(`/api/captions?videoId=${course._doc.id}`)
        .then(response => {
            console.log("Response", response.data)
            setCaptions(response.data)
        })
        .catch(error => console.error('Error fetching captions:', error));
    }
  }, [course]);

  if (!course) {
    return <div>{t('course_data_not_available')}</div>;
  }

  const getVideoEmbedUrl = (url) => {
      if (!url) return '';  // Check if URL is undefined or null

      const videoId = url.split('v=')[1]; // Extract video ID
      if (!videoId) return '';  // If videoId extraction fails, return empty string

      const lang = i18n.language;
      return `https://www.youtube.com/embed/${videoId}?cc_load_policy=1&cc_lang_pref=${lang}`;
  };


  return (
    <div className="video-player">
      <nav className="breadcrumb">
        {/* Navigate back to course selection with preserved preferences */}
        <a href="#" onClick={() => navigate('/courses')}>{t('back_to_course_selection')}</a>
      </nav>
      <h2>{course.title}</h2>
      <div className="video-container">
        <iframe
          width="100%"
          height="480"
          src={getVideoEmbedUrl(course._doc.url)}
          frameBorder="0"
          allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
          allowFullScreen
          title={course.title}
        ></iframe>
      </div>
      <div className="course-details">
        <p><strong>{t('duration')}:</strong> {course._doc.duration}</p>
        <p><strong>{t('level')}:</strong> {course._doc.level}</p>
        <p><strong>{t('source')}:</strong> {course._doc.source}</p>
        <div className="course-topics">
          <p><strong>{t('topic')}:</strong>
          {course._doc.topics.map(topic => (
            <span key={topic} className="course-topic">{topic}</span>
          ))}
          </p>
        </div>
      </div>
    </div>
  );
};

export default VideoPlayer;
