import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import '../../styles/global.css';
import './VideoPlayer.css';

const VideoPlayer = () => {
  const { t } = useTranslation();
  const location = useLocation();
  const navigate = useNavigate();
  const { course } = location.state || {}; // Expect `state` to include course details

  if (!course) {
    return <div>{t('course_data_not_available')}</div>;
  }

  return (
    <div className="video-player">
      <nav className="breadcrumb">
        {/* Navigate back to course selection with preserved preferences */}
        <a href="#" onClick={() => navigate('/courses')}>
          {t('back_to_course_selection')}
        </a>
      </nav>
      <h2>{course.title}</h2>

      {/* Video container */}
      <div className="video-container">
        <video
          width="100%"
          height="480"
          controls
          src={course.content.video.url} // Use the Google Cloud Storage video URL
        >
          {t('your_browser_does_not_support_video')}
        </video>
      </div>

      {/* Course details */}
      <div className="course-details">
        <p><strong>{t('description')}:</strong> {course.description}</p>
        <p><strong>{t('duration')}:</strong> {course.content.video.duration}</p>
        <p><strong>{t('level')}:</strong> {course.level}</p>
        <p><strong>{t('source')}:</strong> {course.source}</p>
        <p className="course-topics">
          {course.topics.map(topic => (
            <span key={topic} className="course-topic">{topic}</span>
          ))}
        </p>
      </div>

      {/* Course content */}
      <div className="course-content">
        <h3>{t('notes')}</h3>
        <iframe
          src={course.content.text.url} // Load the content text file
          width="100%"
          height="200"
          frameBorder="0"
          title={t('course_content')}
          className="content-frame"
        ></iframe>
      </div>
    </div>
  );
};

export default VideoPlayer;
